import numpy as npimport pandas as pdimport timeimport random# [1] data preparationdf1 =  pd.read_csv('./data/GSE71315/GSE71315_scell_ncounts_genes_thresh.csv', index_col=0) # dimension = (genes x cells)df3 = pd.read_csv('./data/human_mRNA_lncRNA/hgnc-search-1640140773563_mRNA.csv', index_col=0) # dimension = (genes x cells)df4 = pd.read_csv('./data/human_mRNA_lncRNA/hgnc-search-1640141131594_lncRNA.csv', index_col=0) # dimension = (genes x cells)# [2] data preprocessingprint("starting preprocessing:")n1 = df1.shape[0] # row  genesn2 = df1.shape[1] # col  cellsm1 = df3.shape[0] # row  genesm2 = df3.shape[1] # col  cellsk1 = df4.shape[0] # row  genesk2 = df4.shape[1] # col  cellsmRNA = df3.loc[:,'Symbol']lncRNA = df4.loc[:,'Symbol']gene_mRNA = [] gene_lncRNA = [] gene_others = [] for i in range(n1):    for m in range(m1):        if df1.index[i] == mRNA[m]:            gene_mRNA.append(df1.index[i])            for i in range(n1):    for k in range(k1):                   if df1.index[i] == lncRNA[k]:             gene_lncRNA.append(df1.index[i]) df=df1.drop(index=gene_mRNA)df=df.drop(index=gene_lncRNA)gene_others = df.index              # [3] get data df10=df1.drop(index=gene_others)# GSE71315_lncRNA#df11=df1.drop(index=gene_mRNA) #df11=df11.drop(index=gene_others)# GSE71315_lncRNA#df12=df1.drop(index=gene_lncRNA)#df12=df12.drop(index=gene_others)# GSE71315_mRNA# [4] save datadf10.to_csv('./data/GSE71315/GSE71315_scell_ncounts_genes_thresh_mRNA_lncRNA.csv')#df11.to_csv('./data/GSE71315/GSE71315_scell_ncounts_genes_thresh_lncRNA.csv')#df12.to_csv('./data/GSE71315/GSE71315_scell_ncounts_genes_thresh_mRNA.csv')